item.description:
#!/bin/bash #Command line calculator Ver0.1
read -t 30 -p &ldquo;Please enter the first operand: &quot; num1 read -t 30 -p &ldquo;Please enter the operator: &quot; op read -t 30 -p &ldquo;Please enter the second operand: &quot; num2
if [ -n &ldquo;$num1&rdquo; -a -n &ldquo;$num2&rdquo; -a -n &ldquo;$op&rdquo; ] then ifInt1=$(echo $num1 | sed ' s/[0-9]//g') ifInt2=$(echo $num2 | sed ' s/[0-9]//g')
if [ &quot;$ifInt1&quot; == &quot;&quot; -a -z &quot;$ifInt2&quot; ] then if [ &quot;$op&quot; == &quot;+&quot; ] then result=$(($num1 + $num2)) elif [ &quot;$op&quot; == &quot;-&quot; ] then result=$(($num1 - $num2)) elif [ &quot;$op&quot; == &quot;*&quot; ] then result=$(($num1 * $num2)) elif [ &quot;$op&quot; == &quot;/&quot; ] then result=$(($num1 / $num2)) else echo &quot;Please enter a valid operator&quot; exit 11 fi else echo &quot;Please enter valid operands&quot; exit 11 fi  else echo &ldquo;Please assign all 3 variables properly&rdquo; exit 11 fi
